import React from 'react';
// import logo from './logo.svg';
import './App.css';

class RangeConversion extends React.Component{

  _handleChange = (e) =>{
    this.props.onRangeChange(e.target.value);
  }

  render(){
    return (
      <>

      </>
    )
  }
}

class App extends React.Component{

  constructor(props){
    super(props)
    this.state = {
      kilometer : 0,
      meter:0,
      milimeter:0
    }
  }
  onSubmit = (event)=>{
    event.preventDefault();
    event.stopPropagation();  

  }

  onChangeKiloMeter = kilometer =>{
    let meter = (parseFloat(kilometer) / 1000).toString();
    let milimeter =  (parseFloat(kilometer) /  (1000 *1000)).toString();
    this.setState({kilometer,meter,milimeter})
  }

  onChangeMeter = meter =>{
    let kilometer =  (parseFloat(meter) * 1000).toString();
    let milimeter =  (parseFloat(meter) /  1000).toString();
    this.setState({kilometer,meter,milimeter})
  }

  onChangeMiliMeter = milimeter =>{
    let kilometer =  (parseFloat(milimeter) * (1000 *1000)).toString();
    let meter =  (parseFloat(milimeter) *  1000).toString();
    this.setState({kilometer,meter,milimeter})
  }


  render(){
    const {kilometer,meter,milimeter} = this.state;

      return (
    <div className="App">
      <h1>Konversi Jarak</h1>
         <RangeConversion curLabel="km" range={kilometer} onRangeChange={this.onChangeKiloMeter}/>

    </div>
  );
  }
}

export default App;
