import React from 'react';
// import logo from './logo.svg';
import './App.css';

class App extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      nama:'',
      gaji:0,
      gender:'perempuan',
      skills:[],
      alamat:'alamat disini'
    }


  }
  onSubmit = (event)=>{
    event.preventDefault();  
      console.log(this.state);
  }
  onChange = (event) =>{
    const {name,value} = event.target;
    this.setState({[name]:value});
  }

  onSelect = (event) =>{
    let skills = [...this.state.skills];

    let idx = skills.findIndex(element => event.target.value === element);
    if(idx > 0){
      skills = [...skills.slice(0,idx),...skills.slice(idx+1,skills.length)]
    }else{
      skills.push(event.target.value);
    }

     this.setState({skills});
  }

  render(){
    const {nama,gaji,gender,alamat} = this.state;

      return (
    <div className="App">
      <h1>Form Laporan</h1>
        <form onSubmit={this.onSubmit}>
          <label>
            Nama<br/>
          <input type="text" name="nama" value={nama} onChange={this.onChange}/>
          </label>
          <label>  Gaji <br/>
       <input type="number" name="gaji" value={gaji} onChange={this.onChange}/>
       </label>
       <label>  Gender  </label>
        <div>
          <label><input type="radio" name="gender" value="laki2" checked={gender ==='laki2'} onChange={this.onChange}/>Laki2</label>
          <label><input type="radio" name="gender"  value="perempuan" checked={gender ==='perempuan'} onChange={this.onChange}/>Perempuan</label>
        </div>

        <label>Skills<br/>


        <select multiple={true} value={this.state.value} onChange={this.onSelect}>
        <option value="petani">Petani</option>
          <option value="pedagang">Pedagang</option>
          <option value="makelar">Makelar</option>
          <option value="supir">Supir</option>
          </select>
        </label>
        </form>

        <label>Alamat <br/>
       <textarea name="alamat" onChange={this.onChange} value={alamat}></textarea>

        </label>
        <button type="submit" onSubmit={this.onSubmit} >Kirimkan</button>
        <div>
          <h1>Isian</h1>
        <ul>
          <li>Nama  :{nama}</li>
          <li>Gaji  :{gaji}</li>
          <li>Gender  :{gender}</li>
          <li>Pekerjaan  :{skills.join(", ")}</li>
          <li>alamat  :{alamat}</li>


        </ul>
        </div>
    </div>
  );
  }
}

export default App;
