import React from 'react';
// import logo from './logo.svg';
import './App.css';
import mobil from './images/mobil.svg';
import motor from './images/motor.svg';

class ProductInfo extends React.Component{

  render(){
    const product = this.props.product;
    const color = product.ready?'blue':'red';
    const image = product.type ==='mobil' ? mobil : motor;

    return (
      <div className="ProductCard" >
        <image src={image}/>
        <div className="ProductInfo" color={color}>
          <span>{product.nama}</span>
          <span>{product.harga}</span>
        </div>
      </div>
    )
  }
}


class ProductTypeRow extends React.Component{
  render(){
    const type = this.props.type;
    return(
    <div className="ProductCategory">{this.props.type}</div>
    )
  }
}

class ProductCards extends React.Component{

  render(){
    const rows = [];
    let lastType = null;

    this.product.products.forEach(product => {
      if (product.type !==lastType){
        rows.push(
          <ProductTypeRow type={product.type} key={product.type} />
        )

        rows.push(
          <ProductInfo product={product} key={product.nama}/>
        )

      }

    });

  }
}

class App extends React.Component{


  render(){
    return (
      <div className="App">

      </div>
    )
  }
}

export default App;
