import React from 'react';
// import logo from './logo.svg';
import './App.css';
import mobil from './images/mobil.svg';
import motor from './images/motor.svg';

class ProductCard extends React.Component{

  render(){
    const product = this.props.product;
    const name = product.nama;
    const type  = product.type;
    const color = product.ready?'blue':'red';
    const image = product.type ==='mobil' ? mobil : motor;

    return (
      <div className="ProductCard" >
        <image src={image}/>
        <div className="ProductInfo" color={color}>
          <span>{name}</span>
          <span>{product.harga}</span>
        </div>
      </div>
    )
  }
}


class App extends React.Component{


  render(){
    return (
      <div className="App">

      </div>
    )
  }
}

export default App;
